  function mpc_notification(){
    var notify, xhr, url, mpcPrev, admId, prev;

    mpcPrev = document.querySelector('.adm-prev');
    prev = mpcPrev.getAttribute('mpc-prevtype');
    admId = mpcPrev.getAttribute('mpc-admId');

    url = __mpc_uri__() + 'functions/mpc-ajax-action.php?qry=CheckAvailNotify&prev=' + prev + '&admId=' + admId;

//alert(mpcPrev);
          notify = document.querySelector('.adm-noti');

          xhr = new XMLHttpRequest();
          xhr.open('GET', url, true) ;
          xhr.onreadystatechange = function() {
              if(this.status == 200 && this.readyState == 4){
                  notify.innerHTML = this.response;
                 //alert(this.response);
              }
          }
          
          xhr.send();


  }



